import { NextRequest, NextResponse } from "next/server";
import { getServiceSupabase } from "@/lib/supabase/client";
import { requireVendor } from "@/lib/auth/middleware";
import { commitMultipleFiles } from "@/lib/deployment/github";
import {
  getStorefrontTemplateFiles,
  rebrandStorefrontFiles,
  createVendorConfigFiles,
  type VendorBranding,
} from "@/lib/deployment/storefront-template";

export async function POST(request: NextRequest) {
  try {
    // Verify vendor authentication
    const authResult = await requireVendor(request);
    if (authResult instanceof NextResponse) {
      return authResult;
    }
    const { vendorId } = authResult;

    const supabase = getServiceSupabase();

    // Get vendor info with branding
    const { data: vendor, error: vendorError } = await supabase
      .from("vendors")
      .select(
        `
        store_name,
        slug,
        github_access_token,
        github_username,
        github_repo_name,
        logo_url,
        brand_colors
      `,
      )
      .eq("id", vendorId)
      .single();

    if (vendorError || !vendor) {
      if (process.env.NODE_ENV === "development") {
        console.error("Vendor not found:", vendorError);
      }
      return NextResponse.json({ error: "Vendor not found" }, { status: 404 });
    }

    if (!vendor.github_access_token || !vendor.github_username) {
      if (process.env.NODE_ENV === "development") {
        console.error("GitHub not connected");
      }
      return NextResponse.json(
        { error: "Please connect your GitHub account first" },
        { status: 400 },
      );
    }

    if (!vendor.github_repo_name) {
      if (process.env.NODE_ENV === "development") {
        console.error("No repo found");
      }
      return NextResponse.json(
        { error: "Please create a repository first" },
        { status: 400 },
      );
    }

    // Get all storefront files
    const templateFiles = await getStorefrontTemplateFiles();

    // Prepare branding info
    const branding: VendorBranding = {
      storeName: vendor.store_name,
      slug: vendor.slug,
      logoUrl: vendor.logo_url,
      brandColors: vendor.brand_colors,
      description: `Premium cannabis products from ${vendor.store_name}`,
    };

    // Rebrand the files
    const rebrandedFiles = rebrandStorefrontFiles(templateFiles, branding);

    // Add config files
    const configFiles = createVendorConfigFiles(branding, vendorId);

    const allFiles = [...rebrandedFiles, ...configFiles];

    // Push all files to the repo
    await commitMultipleFiles(
      vendor.github_access_token,
      vendor.github_username,
      vendor.github_repo_name,
      allFiles,
      "üöÄ Initialize storefront with WhaleTools template\n\nGenerated by WhaleTools platform",
    );

    return NextResponse.json({
      success: true,
      message: "Storefront template pushed successfully",
      filesCount: allFiles.length,
      repo: {
        name: vendor.github_repo_name,
        url: `https://github.com/${vendor.github_username}/${vendor.github_repo_name}`,
      },
    });
  } catch (error: any) {
    if (process.env.NODE_ENV === "development") {
      console.error("‚ùå Error pushing template:", error);
    }
    return NextResponse.json(
      { error: error.message || "Failed to push template" },
      { status: 500 },
    );
  }
}
